import { useState, useEffect } from "react";
import { Layout, Button, message, Card, Image } from "antd";
import { LogoutOutlined, CalendarOutlined } from "@ant-design/icons";
import { getEventConfig } from "../services/api";
import ReservationModal from "../components/ReservationModal";

const { Header, Content } = Layout;

const UserDashboard = ({ user, onLogout }) => {
  const [eventImage, setEventImage] = useState("");
  const [modalVisible, setModalVisible] = useState(false);

  useEffect(() => {
    loadEventConfig();
  }, []);

  const loadEventConfig = async () => {
    try {
      const response = await getEventConfig();
      setEventImage(response.data.event_image || "");
    } catch (error) {
      message.error("Erro ao carregar configuração do evento");
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    message.success("Logout realizado com sucesso!");
    onLogout();
  };

  return (
    <Layout style={{ minHeight: "100vh" }}>
      <Header
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          flexWrap: "wrap",
          padding: "0 16px",
        }}
      >
        <div style={{ color: "white", fontSize: "18px", fontWeight: "bold" }}>
          Reservas
        </div>
        <div
          style={{
            color: "white",
            display: "flex",
            alignItems: "center",
            gap: "8px",
          }}
        >
          <span
            style={{
              display: "inline-block",
              maxWidth: "150px",
              overflow: "hidden",
              textOverflow: "ellipsis",
            }}
          >
            {user.username}
          </span>
          <Button
            type="link"
            icon={<LogoutOutlined />}
            onClick={handleLogout}
            style={{ color: "white", padding: "4px 8px" }}
          >
            <span style={{ display: "none" }}>Sair</span>
          </Button>
        </div>
      </Header>

      <Content style={{ padding: "16px" }}>
        <div style={{ maxWidth: 1200, margin: "0 auto" }}>
          <Card
            cover={
              eventImage ? (
                <Image
                  alt="Evento"
                  src={eventImage}
                  style={{
                    width: "100%",
                    height: "auto",
                    maxHeight: 500,
                    objectFit: "cover",
                  }}
                  fallback="data:image/png;base64,"
                />
              ) : (
                <div
                  style={{
                    height: "auto",
                    minHeight: 200,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    background: "#f0f0f0",
                    fontSize: 16,
                    color: "#999",
                    padding: 20,
                  }}
                >
                  Nenhuma imagem do evento configurada
                </div>
              )
            }
          >
            <div style={{ textAlign: "center", padding: "16px" }}>
              <h2 style={{ fontSize: "clamp(18px, 5vw, 24px)" }}>
                Bem-vindo ao Evento!
              </h2>
              <p
                style={{ fontSize: "clamp(14px, 3vw, 16px)", marginBottom: 20 }}
              >
                Você tem direito a <strong>{user.mesa_quota}</strong> mesa(s) e{" "}
                <strong>{user.cadeira_extra_quota}</strong> cadeira(s) extra.
              </p>
              <Button
                type="primary"
                size="large"
                icon={<CalendarOutlined />}
                onClick={() => setModalVisible(true)}
                style={{ width: "100%", maxWidth: 300 }}
              >
                Gerenciar Minhas Reservas
              </Button>
            </div>
          </Card>
        </div>
      </Content>

      <ReservationModal
        visible={modalVisible}
        onClose={() => setModalVisible(false)}
        user={user}
      />
    </Layout>
  );
};

export default UserDashboard;
